import React from 'react';
import { Plus, Home, Settings, Users, Building, DollarSign, ShieldCheck } from 'lucide-react';

const Sidebar = ({ activeSection, setActiveSection }) => {
  const navigationItems = [
    { id: 'new-chat', label: '+ new Chat', icon: Plus, type: 'action' },
    { id: 'my-projects', label: 'My Projects', icon: Home, type: 'link' },
  ];

  const qventItems = [
    { id: 'systems', label: 'Systems', icon: Settings },
    { id: 'my-prices-dealers', label: 'My Prices (dealers only)', icon: DollarSign },
    { id: 'our-prices-sales', label: 'Our prices (sales team)', icon: DollarSign },
    { id: 'admin-sales', label: 'Admin (sales team)', icon: ShieldCheck },
  ];

  const agrobItems = [
    { id: 'keratwin', label: 'KeraTwin', icon: Building },
    { id: 'my-prices-agrob', label: 'My Prices', icon: DollarSign },
  ];

  const handleNavClick = (id) => {
    setActiveSection(id);
  };

  return (
    <div className="bg-gray-100 text-gray-800 text-sm font-medium w-64 h-screen fixed left-0 top-0 pt-6 pl-6 space-y-2 overflow-y-auto">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-xl font-bold text-gray-900 mb-1">Rawbe</h1>
        <p className="text-gray-600 text-xs">all about your fa√ßade project</p>
      </div>

      {/* Main Navigation */}
      <div className="space-y-2 mb-6">
        {navigationItems.map((item) => (
          <button
            key={item.id}
            onClick={() => handleNavClick(item.id)}
            className={`flex items-center space-x-3 w-full text-left px-3 py-2 rounded-lg transition-colors ${
              activeSection === item.id
                ? 'bg-white text-gray-900 shadow-sm'
                : 'text-gray-700 hover:bg-gray-200'
            }`}
          >
            <item.icon size={16} />
            <span>{item.label}</span>
          </button>
        ))}
      </div>

      {/* Q-VENT Section */}
      <div className="mb-6">
        <div className="mb-3 flex items-center justify-center">
          <img src="/assets/q-vent-logo.svg" alt="Q-VENT" className="h-6 w-auto" />
        </div>
        <div className="space-y-1 ml-2">
          {qventItems.map((item) => (
            <button
              key={item.id}
              onClick={() => handleNavClick(item.id)}
              className={`flex items-center space-x-3 w-full text-left px-3 py-2 rounded-lg transition-colors ${
                activeSection === item.id
                  ? 'bg-white text-gray-900 shadow-sm'
                  : 'text-gray-700 hover:bg-gray-200'
              }`}
            >
              <item.icon size={14} />
              <span>{item.label}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Agrob Buchtal Section */}
      <div className="mb-6">
        <div className="mb-3 flex items-center justify-center">
          <img src="/assets/agrob-buchtal-logo.svg" alt="Agrob Buchtal" className="h-6 w-auto" />
        </div>
        <div className="space-y-1 ml-2">
          {agrobItems.map((item) => (
            <button
              key={item.id}
              onClick={() => handleNavClick(item.id)}
              className={`flex items-center space-x-3 w-full text-left px-3 py-2 rounded-lg transition-colors ${
                activeSection === item.id
                  ? 'bg-white text-gray-900 shadow-sm'
                  : 'text-gray-700 hover:bg-gray-200'
              }`}
            >
              <item.icon size={14} />
              <span>{item.label}</span>
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Sidebar;
